mapper.select:
		select cartno, pname, price, filename, stock, r
		from (
		select cartno, pname, price, filename, stock,
		rownum r
		from(
		select cartno, pname, price, filename, stock
		from contents c left outer join cart ct
		on c.cartno = ct.cartno



					c.pname like '%'||#{word}||'%'


					c.price like '%'||#{word}||'%'


					c.cateno = #{word}



		order by ct.cartno desc
		)

                )where r >= #{sno} and r <= #{eno}
