mapper.select:
		select orderno, pname, price, filename, odate, r
		from (
		select orderno, pname, price, filename, odate,
		rownum r
		from(
		select orderno, pname, price, filename, odate
		from contents c left outer join orders o
		on c.contentsno = o.contentsno



					c.pname like '%'||#{word}||'%'


					c.price like '%'||#{word}||'%'


					c.cateno = #{word}



		order by o.orderno desc
		)

                )where r >= #{sno} and r <= #{eno}
